import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import CreatePostMutation from '../mutations/CreatePostMutation';
import { QueryRenderer, graphql } from 'react-relay';
import environment from '../Environment';


const styles = {
    createPostWrapper: { margin: 30, textAlign: 'center' },
    titleWrapper: {
        display: 'block',
        width: '100%',
        textAlign: 'center',
        fontSize: '30px',
        border: 'none',
        outlineWidth: 0,
    },
    contentWrapper: {
        width: '100%',
        height: '500px',
        fontSize: '20px',
        marginTop: '20px',
    },
    postButtonWrapper: {
        padding: 10,
        background: 'white',
        border: '2px solid indianred',
        color: 'indianred',
        borderRadius: 6,
    },
};

const createPostViewerQuery = graphql`
    query CreatePostViewerQuery{
        viewer{
            id
        }
    }
`;

class CreatePost extends Component {
    state = {
        title: "",
        content: "",
        user: null
    };

    handlePost = viewerId => {
        const { title, content } = this.state;
        CreatePostMutation(title, content, viewerId, () => {
            this.props.history.push('/');
        })
    };

    componentDidMount

    render() {
        return (
            <QueryRenderer
                environment={environment}
                query={createPostViewerQuery}
                render={({ error, props }) => {
                    if (error) {
                        return <div>{error.message}</div>
                    } else if (props) {
                        return (
                            <div style={styles.createPostWrapper}>
                                <input
                                    style={styles.titleWrapper}
                                    value={this.state.title}
                                    placeholder="Title"
                                    onChange={e => this.setState({ title: e.target.value })} />
                                <textarea
                                    style={styles.contentWrapper}
                                    value={this.state.content}
                                    placeholder="Content"
                                    onChange={e =>
                                        this.setState({
                                            content: e.target.value,
                                        })
                                    } />
                                {this.state.title &&
                                    this.state.content && (
                                        <button
                                            style={styles.postButtonWrapper}
                                            onClick={() => this.handlePost(props.viewer.id)}>
                                            Post
                                         </button>
                                    )}
                            </div>
                        )
                    } else {
                        return <div>Loading</div>
                    }
                }} />
        );
    }
}

export default withRouter(CreatePost);
