import React from 'react'
import { Link } from 'react-router-dom';
import { createFragmentContainer, graphql } from 'react-relay';
import Post from './Post';

const styles = {
    listPageWrapper: { marginTop: 20, textAlign: 'center' },
    postButtonWrapper: {
        padding: 10,
        background: 'white',
        border: '2px solid indianred',
        color: 'indianred',
        borderRadius: 6,
        marginTop: 10,
        marginBottom: 10,
    },
};

class ListPage extends React.Component {
    state = {
        user: null
    };

    componentDidMount() {
        const user = localStorage.getItem('User');
        if (user) {
            this.setState({ user: JSON.parse(user) });
        }
    }

    render() {
        return (
            <div style={styles.listPageWrapper}>
                <Link style={styles.postButtonWrapper} to="/create-post">
                    + New Post
                </Link>
                {!this.state.user && (
                    <Link style={styles.buttonWrapper} to="/login">
                        Login
                    </Link>
                )}
                {this.state.user && (
                    <a
                        onClick={() => {
                            localStorage.removeItem('User');
                            window.location.reload();
                        }}
                        style={styles.buttonWrapper}
                    >
                        Logout
                    </a>
                )}
                <div style={{ marginTop: 20 }}>
                    {allPosts.edges.map(({ node }) => {
                        return <Post key={node.__id} post={node} />;
                    })}
                </div>

                <button style={styles.buttonWrapper} onClick={() => this._loadMore()}>
                    Load More
                </button>
            </div>
        );
    }
}

export default createFragmentContainer(
    ListPage,
    {
        viewer: graphql`
        fragment ListPage_viewer on Viewer {
            allPosts(last: 100)
                @connection(key: "ListPage_allPosts", filters: []){
                    edges{
                        node{
                            ...Post_post
                        }
                    }
                }
        }
        `
    }
);
